<script setup>
import { toRefs, ref, onMounted } from 'vue';
const props = defineProps({
  cardType: String,
  cardPoint: String,
  cardRank: String,
  rotate: String, // 是否旋转
})
const { cardType, cardPoint, cardRank, rotate } = toRefs(props)
const needRotate = ref('')

// cardType
// 1 方块 diamond
// 2 梅花 club
// 3 黑桃 spade
// 4 红桃 heart
// bankerCard": "4-8-8|4-9-9", 花色-牌号-点数
const cardNum = {
  "1": "data:image/png;base64,",
  "2": "data:image/png;base64,",
  "3": "data:image/png;base64,",
  "4": "data:image/png;base64,"
}


const rankPosition = {
  '2': "-20px",
  '3': `-40px`,
  '4': `-60px`,
  '5': `-80px`,
  '6': `-100px`,
  '7': `-120px`,
  '8': `-140px`,
  '9': `-160px`,
  '10': `-180px`,
  'J': `-200px`,
  'Q': `-220px`,
  'K': `-240px`,
}


onMounted(async () => {
  // console.log('onMounted-cardOptions', { cardType, cardPoint, cardRank, rotate, rotate });
  needRotate.value = rotate.value
})


</script>

<template>
  <div v-if="cardType && cardPoint && cardRank" class="card"
    :class="{ 'rotate-left': needRotate === 'left', 'rotate-right': needRotate === 'right' }">
    <img :src="cardNum[cardType || '1']" v-bind:style="{ 'left': rankPosition[cardRank] }" alt="poker-card">
  </div>
</template>

<style scoped lang="less">
.card {
  width: 20px;
  height: 30px;
  overflow: hidden;
  will-change: transform;
  transform-origin: left top;
  margin-left: 8px;
  position: relative;

  &.rotate-right {
    transform: rotateX(0) rotate(90deg) translateY(-100%);
    transform-origin: top left;
    margin-right: 10px;
  }

  img {
    position: relative;
    height: 100%;
  }
}
</style>
